-- 设置客户端连接服务器端的编码
SET NAMES UTF8;
-- 丢弃数据库，如果存在
DROP DATABASE IF EXISTS `acw`;
-- 创建数据库  设置编码
CREATE DATABASE `acw` CHARSET=UTF8;
-- 进入数据库
USE `acw`;
-- 商品详情表
CREATE TABLE `acw_details`(
    `did` INT(10) NOT NULL PRIMARY KEY AUTO_INCREMENT COMMENT '商品id',
    `subject` VARCHAR(50) NOT NULL COMMENT '商品标题',
    `taobao` DECIMAL(5,2) NULL COMMENT '淘宝价',
    `jd` DECIMAL(5,2) DEFAULT '100' NULL COMMENT '京东价',
    `price` DECIMAL(5,2) NOT NULL COMMENT '价格',
    `avatar` VARCHAR(50) NOT NULL COMMENT '商品图',
    `category_lid` smallint(5) NOT NULL COMMENT '类型id'
);

CREATE TABLE `acw_category`(
    `lid` smallint(5) NOT NULL PRIMARY KEY AUTO_INCREMENT COMMENT '类型id',
    `category_name` VARCHAR(30) NOT NULL UNIQUE KEY COMMENT '类型名称',
    `category_image` VARCHAR(50) NOT NULL UNIQUE KEY COMMENT '类型图片'
);


CREATE TABLE `acw_image`(
    `image` VARCHAR(255) NOT NULL COMMENT '详情图片',
    `details_did` INT(10) NOT NULL COMMENT '商品id'
);


CREATE TABLE `acw_cart`(
    `id` INT(10) NOT NULL PRIMARY KEY AUTO_INCREMENT COMMENT '购物车商品id',
    `subject` VARCHAR(50) NOT NULL COMMENT '购物车商品标题',
    `avatar` VARCHAR(50) NOT NULL COMMENT '商品图',
    `price` DECIMAL(5,2) NOT NULL COMMENT '价格',
    `number` SMALLINT(5) NOT NULL COMMENT '数量',
    `user_id` INT(10) COMMENT '用户id'
);


CREATE TABLE `acw_user`(
    `id` INT(10) NOT NULL PRIMARY KEY AUTO_INCREMENT COMMENT '用户id',
    `uname` VARCHAR(50) NULL UNIQUE COMMENT '用户名',
    `password` VARCHAR(32) NOT NULL COMMENT '用户密码',
    `phone` CHAR(11) NULL COMMENT '电话号码',
    `avatar` VARCHAR(50) NOT NULL DEFAULT 'unnamed.jpg' COMMENT '用户头像'
);

